import React, { useRef } from 'react';
import './Portfolio.scss';
import { motion, useScroll, useSpring, useTransform } from 'framer-motion';

const items = [
  {
    id: 1,
    title: "AI shoe analyzer",
    img: "data:image/jpeg;base64,",
    desc: "<p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Deserunt, qui! Omnis dolores ipsum error maiores doloremque, in rerum impedit quia nihil harum laboriosam dignissimos quisquam iure aut esse sequi? Ipsam.</p>",

  },
  {
    id: 2,
    title: "AI shoe analyzer",
    img: "data:image/jpeg;base64,",
    desc: "<>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Deserunt, qui! Omnis dolores ipsum error maiores doloremque, in rerum impedit quia nihil harum laboriosam dignissimos quisquam iure aut esse sequi? Ipsam.    ",
  },
 
];

const Single = ({ item }) => {
  const ref = useRef();
  const { scrollYProgress } = useScroll({
     target: ref ,
    offset: [ "start start","end start"]
  });
  const y = useTransform(scrollYProgress, [0, 1], ["0%", "-50%"]);

  return (
    <section ref={ref}>
    <div className="container">
      <div className="wrapper">
        <div className="imgcontainer">
          <img src={item.img} alt={item.title} />
        </div>
        <motion.div className="textcontainer" style={{ y }}>
          <h2>{item.title}</h2>
          <p>{item.desc}</p>
          <button>See Demo</button>
        </motion.div>
      </div>
    </div>
  </section>
  );
};

const Portfolio = () => {
  const ref = useRef();
  const { scrollYProgress } = useScroll({ target: ref, offset: ["end end", "start start"] });
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
  });

  return (
    <div className='portfolio' ref={ref}>
      <motion.div className="progress">
        <h1>Featured Work</h1>
        <motion.div className="progressbar" style={{ scaleX }}></motion.div>
      </motion.div>
      {items.map((item) => (
        <Single item={item} key={item.id} />
      ))}
    </div>
  );
};

export default Portfolio;
